import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Settings.css';

const Settings = () => {
    const [userId] = useState(1); // 임시 사용자 ID
    const [chatHistory, setChatHistory] = useState([]);
    const [userData, setUserData] = useState({name: '', birthDate: '', gender: ''});
    const [isEditing, setIsEditing] = useState(false);
    const [editData, setEditData] = useState({name: '', birthDate: '', gender: ''});

    useEffect(() => {

        const token = localStorage.getItem("token");
        
        // 사용자 정보를 가져오는 API 호출
        axios
            .get(`/api/settings/account/${userId}`)
            .then((response) => {
                setUserData(response.data)
            })
            .catch((error) => console.error('사용자 정보 가져오기 오류:', error));

        // 사용자 챗봇 대화 내역 가져오기
        axios
            .get(`/api/settings/chat-history/${userId}`)
            .then((response) => {
                setChatHistory(response.data);
            })
            .catch((error) => console.error('Error fetching chat history:', error));
    }, [userId]);

    const handleDeleteChatHistory = () => {
        axios
            .delete(`/api/settings/chat-history/${userId}`)
            .then(() => {
                alert('채팅 기록이 성공적으로 삭제되었습니다.');
                setChatHistory([]); // 상태 업데이트
            })
            .catch((error) => {
                console.error('채팅 기록 삭제 중 오류 발생:', error);
                alert('채팅 기록 삭제에 실패했습니다.');
            });
    };

    const handleEdit = () => {
        setEditData(userData); // 현재 데이터를 수정용 데이터로 설정
        setIsEditing(true); // 편집 모드 활성화
    };

    const handleSave = () => {
        axios
            .put(`/api/settings/account/${userId}`, editData)
            .then(() => {
                setUserData(editData); // 수정된 데이터로 업데이트
                setIsEditing(false); // 편집 모드 비활성화
                alert('계정 정보가 성공적으로 업데이트되었습니다.');
            })
            .catch((error) => {
                console.error('계정 정보 업데이트 중 오류 발생:', error);
                alert('계정 정보 업데이트에 실패했습니다.');
            });
    };

    const handleCancel = () => {
        setIsEditing(false); // 편집 모드 비활성화
    };


    return (
        <div className="settings-container">
            <h2>Settings</h2>
            <div className="section">
                <h3>Chat History</h3>
                <button onClick={handleDeleteChatHistory}>Delete Chat History</button>
                <ul>
                    {chatHistory.map((message, index) => (
                        <li key={index}>{message.sender}: {message.message}</li>
                    ))}
                </ul>
            </div>
            <div className="section">
                <h2>Account Information</h2>
                {!isEditing ? (
                    <div className="user-info">
                        <p><strong>Name:</strong> {userData.name}</p>
                        <p><strong>Birth Date:</strong> {userData.birthDate}</p>
                        <p><strong>Gender:</strong> {userData.gender}</p>
                        <button onClick={handleEdit}>Update Account</button>
                    </div>
                ) : (
                    <div className="edit-form">
                        <input
                            type="text"
                            placeholder="Name"
                            value={editData.name}
                            onChange={(e) => setEditData({...editData, name: e.target.value})}
                        />
                        <input
                            type="date"
                            placeholder="Birth Date"
                            value={editData.birthDate}
                            onChange={(e) => setEditData({...editData, birthDate: e.target.value})}
                        />
                        <input
                            type="text"
                            placeholder="Gender"
                            value={editData.gender}
                            onChange={(e) => setEditData({...editData, gender: e.target.value})}
                        />
                        <button onClick={handleSave}>Save</button>
                        <button onClick={handleCancel}>Cancel</button>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Settings;
