import React, {useEffect, useState} from 'react';
import {BrowserRouter as Router, Link, Route, Routes, useNavigate} from "react-router-dom";
import LoginForm from "./components/LoginForm";
import './App.css';
import RegisterForm from "./components/RegisterForm";
import ChatbotInterface from "./components/ChatbotInterface";

function App() {

    return (
        <Router>
            <AppContent/>
        </Router>
    );
}

function AppContent() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (token) {
            setIsLoggedIn(true);
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem("token");
        setIsLoggedIn(false);
        navigate("/login");
    };

  return (
      <Router>
        <div className="App">
          <header className="App-header">
              <nav className="navbar">
                  <div className="navbar-left">
                      <Link to="/" className="navbar-brand">E-Commerce</Link>
                  </div>
                  <div className="navbar-right">
                      {isLoggedIn ? (
                          <>
                            <button className="nav-link button" onClick={handleLogout}>로그아웃</button>
                              <Link to="/settings" className="nav-link button">설정</Link>
                          </>
                      ) : (
                          <>
                            <Link to="/login" className="nav-link button">로그인</Link>
                            <Link to="/register" className="nav-link button">회원가입</Link>
                          </>
                          )}
                  </div>
              </nav>
                <Routes>
                  <Route path="/login" element={<LoginForm/>} />
                    <Route path="/register" element={<RegisterForm/>} />
                  <Route path="/" element={<React.Fragment><h1>Welcome to React App</h1></React.Fragment>} />
                    <Route path="chatbot" element={<ChatbotInterface/>} />
                    <Route path="*" element={<React.Fragment><h1>404: Page Not Found</h1></React.Fragment>} />
                </Routes>
              </header>
            </div>
          </Router>
      );
    }

export default App;
