import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Settings.css';

const Settings = () => {
    const [userId] = useState(1); // 임시 사용자 ID
    const [chatHistory, setChatHistory] = useState([]);
    const [newName, setNewName] = useState('');
    const [newBirthDate, setNewBirthDate] = useState('');
    const [newGender, setNewGender] = useState('');

    useEffect(() => {
        // 사용자 정보를 가져오는 API 호출
        axios
            .get(`/api/settings/account/${userId}`)
            .then((response) => {
                const { name, birthDate, gender } = response.data;
                setNewName(name);
                setNewBirthDate(birthDate);
                setNewGender(gender);
            })
            .catch((error) => console.error('사용자 정보 가져오기 오류:', error));

        // 사용자 챗봇 대화 내역 가져오기
        axios
            .get(`/api/settings/chat-history/${userId}`)
            .then((response) => {
                setChatHistory(response.data);
            })
            .catch((error) => console.error('Error fetching chat history:', error));
    }, [userId]);

    const handleDeleteChatHistory = () => {
        axios
            .delete(`/api/settings/chat-history/${userId}`)
            .then(() => {
                alert('채팅 기록이 성공적으로 삭제되었습니다.');
                setChatHistory([]); // 상태 업데이트
            })
            .catch((error) => {
                console.error('채팅 기록 삭제 중 오류 발생:', error);
                alert('채팅 기록 삭제에 실패했습니다.');
            });
    };

    const handleUpdateAccount = () => {
        const updateData = {
            newName,
            newBirthDate,
            newGender,
        };

        axios
            .put(`/api/settings/account/${userId}`, updateData)
            .then(() => {
                alert('계정 정보가 성공적으로 업데이트되었습니다.');
            })
            .catch((error) => {
                console.error('계정 정보 업데이트 중 오류 발생:', error);
                alert('계정 정보 업데이트에 실패했습니다.');
            });
    };

    return (
        <div className="settings-container">
            <h2>Settings</h2>
            <div className="section">
                <h3>Chat History</h3>
                <button onClick={handleDeleteChatHistory}>Delete Chat History</button>
                <ul>
                    {chatHistory.map((message, index) => (
                        <li key={index}>{message.sender}: {message.message}</li>
                    ))}
                </ul>
            </div>
            <div className="section">
                <h3>Update Account</h3>
                <input
                    type="text"
                    placeholder="New Name"
                    value={newName}
                    onChange={(e) => setNewName(e.target.value)}
                />
                <input
                    type="date"
                    placeholder="New Birth Date"
                    value={newBirthDate}
                    onChange={(e) => setNewBirthDate(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="New Gender"
                    value={newGender}
                    onChange={(e) => setNewGender(e.target.value)}
                />
                <button onClick={handleUpdateAccount}>Update Account</button>
            </div>
        </div>
    );
};

export default Settings;
