import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Link, Route, Routes, useNavigate, Navigate } from "react-router-dom";
import LoginForm from "./components/LoginForm";
import './App.css'; // 전역 스타일
import RegisterForm from "./components/RegisterForm";
import ChatbotInterface from "./components/ChatbotInterface";
import MainBoard from "./components/MainBoard";
import Settings from "./components/Settings";
import ReviewPage from "./components/ReviewPage";
import DashBoard from "./components/DashBoard";

function App() {
    return (
        <Router>
            <AppContent />
        </Router>
    );
}

function AppContent() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const navigate = useNavigate();

    // 로그인 상태 확인 및 초기 리다이렉트
    useEffect(() => {
        const token = localStorage.getItem("token");
        setIsLoggedIn(!!token); // 토큰이 존재하면 true, 없으면 false
        if (!token) {
            navigate("/login"); // 로그아웃 상태라면 /login으로 이동
        } else {
            navigate("/mainboard"); // 로그인 상태라면 /mainboard로 이동
        }
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem("token");
        setIsLoggedIn(false);
        navigate("/login");
    };

    return (
        <div className="App">
            <header className="App-header">
                <nav className="navbar">
                    <div className="navbar-left">
                        <Link to="/" className="navbar-brand">E-Commerce</Link>
                    </div>
                    <div className="navbar-right">
                        {isLoggedIn ? (
                            <>
                                <button className="nav-link button" onClick={handleLogout}>로그아웃</button>
                                <Link to="/mainboard" className="nav-link button">메인 화면</Link>
                            </>
                        ) : (
                            <>
                                <Link to="/login" className="nav-link button">로그인</Link>
                                <Link to="/register" className="nav-link button">회원가입</Link>
                            </>
                        )}
                    </div>
                </nav>
                <Routes>
                    {/* 로그인 및 회원가입은 로그아웃 상태에서만 접근 가능 */}
                    <Route path="/login" element={!isLoggedIn ? <LoginForm /> : <Navigate to="/mainboard" />} />
                    <Route path="/register" element={!isLoggedIn ? <RegisterForm /> : <Navigate to="/mainboard" />} />

                    {/* 로그인 상태에서만 접근 가능 */}
                    <Route path="/mainboard" element={isLoggedIn ? <MainBoard /> : <Navigate to="/login" />} />
                    <Route path="/chatbot" element={isLoggedIn ? <ChatbotInterface /> : <Navigate to="/login" />} />
                    <Route path="/settings" element={isLoggedIn ? <Settings /> : <Navigate to="/login" />} />
                    <Route path="/review-items" element={isLoggedIn ? <ReviewPage /> : <Navigate to="/login" />} />
                    <Route path="/dashboard" element={isLoggedIn ? <DashBoard /> : <Navigate to="/login" />} />

                    {/* 기본 경로 처리 */}
                    <Route path="*" element={<Navigate to="/" />} />
                </Routes>
            </header>
        </div>
    );
}

export default App;
