import React, { useEffect, useState } from 'react';
import axios from 'axios';

const SuccessPage = () => {
    const [data, setData] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem('token'); // 로그인 후 저장된 토큰 가져오기

        if (token) {
            axios.get('http://localhost:8080/success', { // 보호된 리소스 엔드포인트
                headers: {
                    Authorization: `Bearer ${token}` // 토큰을 Authorization 헤더에 추가
                }
            })
                .then(response => {
                    setData(response.data); // 응답 데이터 설정
                })
                .catch(error => {
                    console.error('Error accessing protected resource:', error);
                    setError('Access to protected resource failed.'); // 에러 메시지 설정
                });
        } else {
            setError('No token found. Please log in again.'); // 토큰이 없으면 에러 메시지 설정
        }
    }, []);

    if (error) {
        return <div>{error}</div>; // 에러 메시지 표시
    }

    if (!data) {
        return <div>Loading...</div>; // 데이터 로딩 중 메시지 표시
    }

    return (
        <div>
            <h1>로그인 성공</h1>
            <p>Protected data: {JSON.stringify(data)}</p> {/* 보호된 리소스의 데이터 표시 */}
        </div>
    );
};

export default SuccessPage;
