import React, { useState, useEffect } from "react";
import axios from "axios";
import "./DashBoard.css";
import { PieChart, Pie, Cell, BarChart, Bar, XAxis, YAxis, Tooltip, Legend } from "recharts";

const DashBoard = () => {
    const [recommendations, setRecommendations] = useState([]);
    const [items, setItems] = useState([]); // 구매한 제품 리스트
    const [profile, setProfile] = useState(null);
    const [statistics, setStatistics] = useState(null); // 사용자 통계 데이터
    const COLORS = ["#8884d8", "#82ca9d", "#ffc658", "#ff8042", "#0088FE"]; // 차트 색상

    useEffect(() => {
        const token = localStorage.getItem("token"); // JWT 토큰 가져오기
        if (!token) {
            alert("로그인이 필요합니다. 다시 로그인해 주세요.");
            window.location.href = "/login";
            return;
        }

        // 추천 제품 가져오기
        axios
            .get("http://localhost:8080/api/dashboard/recommendations_user", {
                headers: { Authorization: `Bearer ${token}` },
            })
            .then((response) => {
                setRecommendations(response.data);
            })
            .catch((err) => {
                console.error("추천 정보를 가져오는 데 실패했습니다:", err);
            });

        axios
            .get("http://localhost:8080/api/dashboard/statistics", {
                headers: { Authorization: `Bearer ${token}` },
            })
            .then((response) => {
                setStatistics(response.data); // 통계 데이터 저장
            })
            .catch((err) => {
                console.error("사용자 통계를 가져오는 데 실패했습니다:", err);
            });

        // 프로필 정보 가져오기
        axios
            .get("http://localhost:8080/api/dashboard/profile", {
                headers: { Authorization: `Bearer ${token}` },
            })
            .then((response) => {
                setProfile(response.data);
            })
            .catch((err) => {
                console.error("프로필 정보를 가져오는 데 실패했습니다:", err);
            });
    }, []);

    const handleConfirmPurchase = (itemId) => {
        const token = localStorage.getItem("token");
        axios
            .post(
                "http://localhost:8080/api/review/confirm",
                {itemId},
                {headers: {Authorization: `Bearer ${token}`}}
            )
            .then(() => {
                alert("구매가 성공적으로 확정되었습니다.");
                setItems((prevItems) =>
                    prevItems.map((item) =>
                        item.itemId === itemId
                            ? {...item, isPurchased: true}
                            : item
                    )
                );
            })
            .catch((err) => {
                console.error("구매 확정 실패:", err);
                alert("구매를 확정하는 데 실패했습니다.");
            });

    };



    return (
        <div className="dashboard-container">
            {/* 프로필 섹션 */}
            {profile && (
                <div className="profile-section">
                    <img
                        src={"https://koreansandwich-ecommerce-item-image.s3.ap-southeast-2.amazonaws.com/cute-profile-image.png"}
                        alt="프로필"
                        onError={(e) => (e.target.src = "/default-profile.png")}
                    />
                    <div className="profile-info">
                        <br/>
                        <p>{profile.name}</p>
                        <p>{profile.age} / {profile.gender}</p>
                    </div>
                </div>
            )}

            {/* 구매 통계 섹션 */}
            { statistics && (
                <div className="statistics-section">
                    <h2>사용자 구매 통계</h2>

                    {/* 카드 형태로 간단한 데이터 표시 */}
                    <div className="statistics-cards">
                        <div className="card">
                            <h3>구매 횟수</h3>
                            <p>{statistics.purchaseCount}</p>
                        </div>
                        <div className="card">
                            <h3>리뷰 개수</h3>
                            <p>{statistics.reviewCount}</p>
                        </div>
                        <div className="card">
                            <h3>평균 별점</h3>
                            <p>{statistics.averageRating.toFixed(2)}</p>
                        </div>
                    </div>
                    {/* 구매 카테고리 - 원형 차트 */}
                    <h3>구매한 제품 카테고리</h3>
                    <PieChart width={400} height={300}>
                        <Pie
                            data={statistics.purchasedCategories.split(", ").map((category) => ({
                                name: category,
                                value: 1, // 카테고리별 비율 계산 필요 시 조정 가능
                            }))}
                            dataKey="value"
                            nameKey="name"
                            cx="50%"
                            cy="50%"
                            outerRadius={100}
                            fill="#8884d8"
                        >
                            {statistics.purchasedCategories.split(", ").map((_, index) => (
                                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                            ))}
                        </Pie>
                        <Tooltip />
                        <Legend />
                    </PieChart>

                    {/* 구매한 제품별 별점 분포 - 막대 차트 */}
                    <h3>평균 별점 분포</h3>
                    <BarChart
                        width={400}
                        height={300}
                        data={[
                            { name: "1점", value: 2 },
                            { name: "2점", value: 1 },
                            { name: "3점", value: 5 },
                            { name: "4점", value: 8 },
                            { name: "5점", value: 12 },
                        ]} // 샘플 데이터, 실제 데이터로 대체 필요
                    >
                        <XAxis dataKey="name" />
                        <YAxis />
                        <Tooltip />
                        <Legend />
                        <Bar dataKey="value" fill="#82ca9d" />
                    </BarChart>
                </div>
            )}

            {/* 추천 제품 섹션 */}
            <div className="recommendations-section">
                <h2>추천 제품</h2>
                <div className="items-grid">
                    {recommendations.map((item) => (
                        <div key={item.itemId} className="item-card">
                            <img
                                src={item.itemImageUrl}
                                alt={item.itemName}
                                onClick={() => window.open(item.itemLink, "_blank")}
                                style={{cursor: "pointer"}} // 이미지 클릭 시 커서 표시
                            />
                            <h3>{item.itemName}</h3>
                            <p>가격: {item.itemFinalPrice}원</p>
                            <button
                                onClick={() => handleConfirmPurchase(item.itemId)}
                                className="purchase-button"
                            >
                                구매 확정
                            </button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default DashBoard;
