import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import './ChangePassword.css';

// 유효성 검사 스키마
const ChangePasswordSchema = Yup.object().shape({
    currentPassword: Yup.string().required('현재 비밀번호를 입력하세요'),
    newPassword: Yup.string()
        .min(8, '비밀번호는 8자리 이상이어야 합니다.')
        .matches(/^(?=.*[a-zA-Z])(?=.*\d)[a-zA-Z\d]{8,}$/, '비밀번호는 영문자와 숫자를 포함해야 합니다.')
        .required('새 비밀번호를 입력하세요'),
    confirmPassword: Yup.string()
        .oneOf([Yup.ref('newPassword'), null], '비밀번호가 일치하지 않습니다.')
        .required('새 비밀번호를 확인하세요'),
});

const ChangePassword = () => {
    const handleSubmit = (values, { setSubmitting }) => {
        const token = localStorage.getItem('token'); // JWT 토큰 가져오기
        axios
            .put('http://localhost:8080/api/settings/change-password', values, {
                headers: { Authorization: `Bearer ${token}` },
            })
            .then(() => {
                alert('비밀번호가 성공적으로 변경되었습니다.');
                setSubmitting(false);
            })
            .catch((error) => {
                console.error('비밀번호 변경 오류:', error);
                alert('비밀번호 변경에 실패했습니다.');
                setSubmitting(false);
            });
    };

    return (
        <div className="change-password-container">
            <h2>Change Password</h2>
            <Formik
                initialValues={{ currentPassword: '', newPassword: '', confirmPassword: '' }}
                validationSchema={ChangePasswordSchema}
                onSubmit={handleSubmit}
            >
                {({ isSubmitting }) => (
                    <Form>
                        <div className="form-field">
                            <Field type="password" name="currentPassword" placeholder="현재 비밀번호" />
                            <ErrorMessage name="currentPassword" component="div" className="error-message" />
                        </div>
                        <div className="form-field">
                            <Field type="password" name="newPassword" placeholder="새 비밀번호" />
                            <ErrorMessage name="newPassword" component="div" className="error-message" />
                        </div>
                        <div className="form-field">
                            <Field type="password" name="confirmPassword" placeholder="새 비밀번호 확인" />
                            <ErrorMessage name="confirmPassword" component="div" className="error-message" />
                        </div>
                        <button type="submit" disabled={isSubmitting}>Change Password</button>
                    </Form>
                )}
            </Formik>
        </div>
    );
};

export default ChangePassword;
