import React, { useState, useEffect } from "react";
import axios from "axios";
import "./DashBoard.css";

const DashBoard = () => {
    const [recommendations, setRecommendations] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState("");
    const [error, setError] = useState("");

    useEffect(() => {
        const token = localStorage.getItem("token"); // JWT 토큰 가져오기
        if (!token) {
            alert("로그인이 필요합니다. 다시 로그인해 주세요.");
            window.location.href = "/login";
            return;
        }

        // 카테고리별 추천 제품 가져오기
        axios
            .get("http://localhost:8080/api/dashboard/recommendations_user", {
                headers: { Authorization: `Bearer ${token}` },
            })
            .then((response) => {
                setRecommendations(response.data);
            })
            .catch((err) => {
                console.error("추천 정보를 가져오는 데 실패했습니다:", err);
                setError("추천 정보를 가져오는 데 실패했습니다.");
            });
    }, [selectedCategory]);

    const handleCategoryClick = (category) => {
        setSelectedCategory(category);
    };

    return (
        <div className="dashboard-container">
            <h1>대시보드</h1>
            {error && <p className="error-message">{error}</p>}
            <div className="categories">
                {Object.keys(recommendations).map((category) => (
                    <button
                        key={category}
                        onClick={() => handleCategoryClick(category)}
                        className={`category-button ${selectedCategory === category ? "active" : ""}`}
                    >
                        {category}
                    </button>
                ))}
            </div>
            <div className="recommendations">
                {recommendations[selectedCategory]?.map((item) => (
                    <div key={item.itemId} className="recommendation-card">
                        <img
                            src={item.itemImageUrl}
                            alt={item.itemName}
                            className="recommendation-image"
                            onClick={() => window.open(item.itemLink, "_blank")}
                        />
                        <h3>{item.itemName}</h3>
                        <p>가격: {item.itemFinalPrice}원</p>
                        <p>브랜드: {item.brand}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default DashBoard;
